import React from 'react';
import ListItem from "./ListItem";

class List extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            items: []
        }
    }

    componentDidMount() {
        fetch("http://localhost:8080/demo/list")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        items: result.items
                    })
                }
            )
    }

    render() {
        return this.state.items.map((item) =>
            <ListItem id={item.id} category={item.category}/>
        );
    }
}

export default List;
