import React from 'react';
import ListItem from "./ListItem";
import FilterList from "./FilterList";

class List extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            items: []
        }
    }

    componentDidMount() {
        fetch("http://localhost:8080/demo/list")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        items: result.items
                    })
                }
            )
    }

    render() {
        return (
            <div>
                <FilterList filterList={this.filterList}/>;
                this.state.items.map((item) => <ListItem key={item.id} category={item.category}/>)
            </div>
        );
    }
}

export default List;
